--
-- 表的结构 `blog_type`
--

CREATE TABLE `blog_type` (
  `id` int(11) NOT NULL COMMENT '类型id',
  `class_name` char(100) NOT NULL COMMENT '类型名称',
  `sort` int(11) NOT NULL DEFAULT '100' COMMENT '排序值越小越靠前',
  `status` tinyint(4) DEFAULT NULL COMMENT '1有效0失效'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=FIXED;

--
-- 转存表中的数据 `blog_type`
--

INSERT INTO `blog_type` (`id`, `class_name`, `sort`, `status`) VALUES
(0, '人文风景', 0, 1),
(1, '世界风景', 1, 1),
(2, '其他', 2, 1);

